<!DOCTYPE html>
<html>
<head>
	<title>MalesBaca - ADMIN LOGIN</title>
	<meta name="csrf-token" content="{{ csrf_token() }}">
	<link href="/css/app.css" rel="stylesheet">
	<link href="/css/login.css" rel="stylesheet">
</head>
<body>


	<div class="wrapper fadeInDown">
		<div id="formContent">
			<!-- Tabs Titles -->

			<!-- Icon -->
			<div class="fadeIn first p-3">
				<img src="data:image/svg+xml;base64," width="80"/>
			</div>

			<!-- Login Form -->
			<form  method="post" action="{{ route('adminloginCheck') }}">
				{{ csrf_field() }}
				<input type="text" id="id" class="fadeIn second" name="id" placeholder="ID Admin" required>
				<input type="password" id="password" class="fadeIn third" name="password" placeholder="Password" required>
				<input type="submit" class="fadeIn fourth" value="input">
			</form>

			<!-- Remind Passowrd -->
			<div id="formFooter">
			<a class="underlineHover" href="tel:085145814955">Forgot password? Contact owner.</a>
			</div>

		</div>
	</div>

	<script src="/js/app.js"></script>
</body>
</html>